package day5;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Print 1 to 10 numbers
		
		int i=1;
		do {
			System.out.println(i);
			i++;
		}while(i<=10);
		
		//Print 10 to 1 descending order
		int j=10;
		do {
			System.out.println(j);
			j--;
		}while(j>0);
		
	}

}
